import { ApplicationInstall } from '@orchesty/nodejs-sdk/dist/lib/Application/Database/ApplicationInstall';
import AGoogle from '../AGoogle';

export const BASE_URL = 'https://www.googleapis.com';

export default class GoogleCalendarApplication extends AGoogle {

    public getName(): string {
        return 'google-Calendar';
    }

    public getPublicName(): string {
        return 'GoogleCalendar';
    }

    public getBaseUrl(): string {
        return BASE_URL;
    }

    public getDescription(): string {
        return 'Organize your schedule and share events with your co-workers and friends';
    }

    public getLogo(): string {
        return 'data:image/svg+xml;base64,';
    }

    // eslint-disable-next-line @typescript-eslint/no-unused-vars
    public getScopes(applicationInstall: ApplicationInstall): string[] {
        return ['https://www.googleapis.com/auth/calendar'];
    }

}
